x = ""

# A, B, C -> Rock, Paper, Scissors -- response
# X, Y, Z -> Rock, Paper, Scissors -- feedback
# Rock - 1
# Paper - 2
# Scissors - 3
# Loose - 0
# Draw - 3
# Win - 6

def powers(x:str, y:str)->int:
    """A function to determine the powers of each pull,
    and calculate the points after each round"""

    wins = {
        "A": ["Y", 8],
        "B": ["Z", 9],
        "C": ["X", 7],
    }

    draws = {
        "A": ["X", 4],
        "B": ["Y", 5],
        "C": ["Z", 6],
    }
    
    losses = {
        "X": 1,
        "Y": 2,
        "Z": 3,
    }
    
    if(wins[x][0] == y):
        return wins[x][1]
    elif(draws[x][0] == y):
        return draws[x][1]
    else:
        return losses[y]

def strategy_points(x:str):

    opponents_choice = ""

    total_points = 0

    for i in range(len(x)):
        if(i % 2 == 0):
            opponents_choice = x[i]
        else:
            total_points += powers(opponents_choice, x[i])
    return total_points

print(strategy_points(x))
